- (void) setLDrawColor:(LDrawColor *)newColor
{
    // Store the color
    [newColor retain];
    [self->color release];
    self->color = newColor;

    [self colorSynthesizedPartsTranslucent:[self isSelected]];
}//end setLDrawColor:

